mapper.insert:
INSERT INTO group_chat_room_member (
    group_chat_room_id,
    member_id
) VALUES (
    #{groupChatRoomId},
    #{memberId}
)

mapper.select:
SELECT ca.chat_archive_id AS chatArchiveId, ca.chat_archive_title AS chatArchiveTitle, ca.chat_archive_date AS chatArchiveDate, ca.is_chat_archive_bookmarks AS isChatArchiveBookmarks FROM chat_archive ca WHERE ca.member_id = #{memberId}
AND ca.is_chat_archive_bookmarks = true
ORDER BY ca.chat_archive_id DESC LIMIT #{size} OFFSET #{offset};

SELECT gc.group_chat_room_id AS groupChatRoomId, gc.group_chat_room_title AS groupChatRoomTitle, gc.group_chat_room_topic AS groupChatRoomTopic, gc.group_chat_room_create_date AS groupChatRoomCreateDate, COUNT(gcm.member_id) AS currentParticipants FROM group_chat_room gc LEFT JOIN group_chat_room_member gcm ON gc.group_chat_room_id = gcm.group_chat_room_id
gc.group_chat_room_topic = #{groupChatRoomTopic}
AND REPLACE(LOWER(gc.group_chat_room_title), ' ', '') LIKE CONCAT('%', REPLACE(LOWER(#{searchText}), ' ', ''), '%')
GROUP BY gc.group_chat_room_id, gc.group_chat_room_title, gc.group_chat_room_max_participants, gc.group_chat_room_topic, gc.group_chat_room_create_date ORDER BY gc.group_chat_room_id DESC LIMIT #{size} OFFSET #{offset};

SELECT COUNT(DISTINCT gc.group_chat_room_id) FROM group_chat_room gc LEFT JOIN group_chat_room_member gcm ON gc.group_chat_room_id = gcm.group_chat_room_id
gc.group_chat_room_topic = #{groupChatRoomTopic}
AND REPLACE(LOWER(gc.group_chat_room_title), ' ', '') LIKE CONCAT('%', REPLACE(LOWER(#{searchText}), ' ', ''), '%')

mapper.delete:
DELETE FROM
    group_chat_room_member
WHERE
    group_chat_room_id = #{groupChatRoomId}
AND
    member_id = #{memberId}
